{% block title %} home {% endblock %}

{% block content %}
    <div class=\"container mt-5\">
        <h2> Profile </h2>
        <div class=\"row\">
            <div class=\"col-lg-4\">

            </div>
            
            <div class=\"col-lg-8\">
                <table class=\"table\">        
                <tbody>
                    <tr>
                    <th scope=\"row\">Username</th>
                    <td>{{ app.user.username }}</td>
                    <td></td>
                    <td></td>
                    </tr>
                    <tr>
                    <th scope=\"row\">Email</th>
                    <td>{{ app.user.email }}</td>
                    <td></td>
                    <td></td>
                    </tr>
                    <tr>
                    <th scope=\"row\">Account status</th>
                    {% if app.user.active %}
                        <td> <span class=\"badge badge-success\">Confirmed</span> </td>
                    {% else %}
                        <td> <span class=\"badge badge-danger\">Not confirmed</span> </td>
                    {% endif %}
                    <td></td>
                    <td></td>
                    </tr>
                </tbody>
                </table>

            </div>
        </div>
        
    </div>    
{% endblock %}", "profile/index.html.twig", "/opt/lampp/htdocs/learn/symfony-project/auth/templates/profile/index.html.twig");
