        return new Source("{% extends 'base.html.twig' %}

{% block title %}Log in!{% endblock %}

{% block content %}
<div class=\"container mt-5\">
    <div class=\"row\">
        <div class=\"col-md-6 offset-md-3\">
            
            <h1 class=\"h3 mb-3 font-weight-normal\">Forgot Password !</h1>
            {% for label, messages in app.flashes %}
                {% for message in messages %}
                    <div class=\"alert alert-{{ label }}\">
                        {{ message }}
                    </div>
                {% endfor %}
            {% endfor %}
            
            {{ form_start(formForgotPassword) }}
            <!--<form method=\"post\">-->                

                <!--<div class=\"form-group\">
                    <input type=\"email\"  name=\"email\" id=\"inputEmail\" class=\"form-control\" placeholder=\"Email\" required autofocus>
                </div>-->
                {{ form_row(formForgotPassword.email) }}
                        
                <!-- <input type=\"hidden\" name=\"_csrf_token\" value=\"{# csrf_token('authenticate') #}\">--> 

                <button class=\"btn btn-primary mt-1\" type=\"submit\"> Send  </button>
            <!--</form>-->
            {{ form_end(formForgotPassword) }}

        </div>    
     </div>   
</div>
{% endblock %}", "security/forgotPassword.html.twig", "/opt/lampp/htdocs/learn/symfony-project/auth/templates/security/forgotPassword.html.twig");
